function showAlert(e, t) { switch (e) { case "success": const a = createAlert("success", t); $(".message-container").html(a); break; case "warning": const r = createAlert("warning", t); $(".message-container").html(r); break; case "danger": const n = createAlert("danger", t); $(".message-container").html(n); break; case "danger-modal": const s = createAlert("danger", t); $(".danger-container-modal").html(s) } } function createErrorMessageValidation(e) { let t = $("<ul></ul>"); const a = e.map(e => `<li>${e}</li>`); return t.html(a), `<p>There are some errors, please verify:</p>${t.html()}` } function createAlert(e, t) { return `<div class="alert alert-${e} alert-dismissible fade show" role="alert">\n                        <div>${t}</div>\n                        <button type="button" class="close" data-dismiss="alert" aria-label="Close">\n                            <span aria-hidden="true">&times;</span>\n                        </button>\n                   </div>` } function cleanModalMessage() { $(".danger-container-modal").html("") } function getParameters(e) { if ("" === e) return null; const t = {}, a = (e = e.substring(1)).split("&"); for (let e of a) { const a = e.split("="); t[a[0]] = a[1] } return t } function setDataUserId(e) { window.localStorage.setItem("userId", e) } function setDataUserEmail(e) { window.localStorage.setItem("userEmail", e) } function clearLocalStorage() { window.localStorage.clear() }